// Mode: Run Once (JavaScript)
// Input: previous node items via $input.all()
// Output: all non-video ads; adds imageUrl if found, but does not drop items without images.

function get(obj, path, def = undefined) {
  if (!obj || typeof path !== 'string') return def;
  return path.split('.').reduce((acc, k) => (acc && acc[k] !== undefined ? acc[k] : undefined), obj) ?? def;
}

function isHttpUrl(u) {
  return typeof u === 'string' && /^https?:\/\/\S+/i.test(u);
}

function isVideoByFormat(ad) {
  const fmt = (get(ad, 'snapshot.display_format', '') || '').toString().toUpperCase();
  return fmt === 'VIDEO';
}

// Try to pick an image URL if present, but don't require it
function pickImageUrl(ad) {
  // Common spots in Ad Library snapshots
  const candidates = []
    .concat((get(ad, 'snapshot.images') || []).map(i => i?.url).filter(Boolean))
    .concat(get(ad, 'snapshot.thumbnail_url') || [])
    .concat(get(ad, 'creative.image_url') || [])
    .concat(get(ad, 'image_url') || [])
    .concat(get(ad, 'thumbnail_url') || []);

  const urls = Array.isArray(candidates) ? candidates : [candidates];
  for (const u of urls) {
    if (isHttpUrl(u) && !/^data:/i.test(u)) return u;
  }
  return null;
}

const inputItems = $input.all();
const out = [];

for (const item of inputItems) {
  const ad = item.json || item;

  // Keep only non-video by display_format
  if (isVideoByFormat(ad)) continue;

  const imageUrl = pickImageUrl(ad); // may be null
  out.push({
    json: {
      ...ad,
      imageUrl, // optional; present when detectable
    },
  });
}

// If none found, emit a clear status so you see why
if (out.length === 0) {
  return [{
    json: {
      error: 'NO_NON_VIDEO_ADS',
      message: 'All items are video by snapshot.display_format; no non-video ads in this batch.',
      sourceCount: inputItems.length,
    },
  }];
}

return out;

