// Mode: Run Once (JavaScript)
// Input: $json.content.parts[0].text
// Output: { cleanedPrompt: "<JSON-safe string>" }

function stripMarkdown(text) {
  if (typeof text !== 'string') text = String(text ?? '');

  // Remove fenced code blocks ```...```
  text = text.replace(/```[\s\S]*?```/g, '');
  // Remove inline code backticks `
  text = text.replace(/`+/g, '');

  // Remove headings like #### Title or ## Title
  text = text.replace(/^[ \t]*#{1,6}[ \t]+/gm, '');

  // Remove bold/italic markers **text**, *text*, __text__, _text_
  text = text.replace(/\*\*/g, '')
             .replace(/\*/g, '')
             .replace(/__+/g, '')
             .replace(/_+/g, '');

  // Remove horizontal rules --- or ***
  text = text.replace(/^[ \t]*([-*_]){3,}[ \t]*$/gm, '');

  // Remove list markers (-, *, +) at line start and ordered list "1. "
  text = text.replace(/^[ \t]*[-*+][ \t]+/gm, '')
             .replace(/^[ \t]*\d+\.[ \t]+/gm, '');

  // Trim trailing/leading spaces per line
  text = text.replace(/[ \t]+$/gm, '');

  return text;
}

function cleanForJsonString(text) {
  text = stripMarkdown(text);

  // Normalize CRLF/CR to LF
  text = text.replace(/\r\n/g, '\n').replace(/\r/g, '\n');

  // Escape backslashes first
  text = text.replace(/\\/g, '\\\\');

  // Escape double quotes
  text = text.replace(/"/g, '\\"');

  // Convert real newlines to literal \n
  text = text.replace(/\n/g, '\\n');

  // Collapse excessive spaces
  text = text.replace(/[ \t]{2,}/g, ' ').trim();

  return text;
}

// Read prompt safely
const rawPrompt =
  $json?.content?.parts?.[0]?.text ??
  $json?.prompt ??
  '';

// Produce cleaned prompt
const cleanedPrompt = cleanForJsonString(rawPrompt);

// Return to downstream nodes
return [
  {
    json: { cleanedPrompt },
  },
];
